import * as api from "api";
import * as action from "actions";

export const setTasks = (data) => ({
  type: "SET_TASKS",
  payload: data,
});

export const fetchCreateTask = (data) => (dispatch) => {
  dispatch(action.showLoading());
  return api
    .createTask(data)
    .then((response) => {
      //get task
      dispatch(action.hideLoading());
    })
    .catch((error) => {
      if (error.code === 401) {
        dispatch(action.setAuth(null));
      }
      dispatch(action.setError(error.message));
    })
    .finally(() => {
      dispatch(action.hideLoading());
    });
};


export const processingTasks = (type, data) => (dispatch) => {
  dispatch(action.showLoading());
  return api
    .processingTasks(type, data.taskId)
    .then((response) => {
      dispatch(action.hideLoading());
    })
    .catch((error) => {
      dispatch(action.setError(error.message));
    })  
    .finally(() => {
      alert("rtyuio")
      dispatch(action.hideLoading());
      // db.cache.put({ id: "tasks", data: entities }).then(() => {
      
      // });
      db.cache.get("tasks").then((cache) => {
        const datatime = cache.data;
        datatime["masterDataRefreshTime"] = currentRefreshTime();
        db.cache.put({ id: "dataRefreshTime", data: datatime }).then(() => {
          dispatch(action.setAllDataRefreshTime(datatime));
        });
      });


    });
}
