import { Box, FormLabel, makeStyles, Typography } from "@material-ui/core";
import Compressor from "compressorjs";
import { LocalizeContext } from "i18n";
import { fillTextToImg } from "js/util";
import React, { useRef, useState } from "react";
import { useParams } from 'react-router-dom';

const useStyles = makeStyles(({ spacing }) => ({
  btm: {
    display: "flex",
    justifyContent: "space-between",
    margin: spacing(1, 1, 0, 1),
  },
  root: {
    display: "grid",
    padding: spacing(1, 0),
    borderBottom: " 1px solid #e0e0e0",
    backgroundColor: "#FFF",
  },
  lable: {
    margin: spacing(0, 0, 1, 0),
    color: "rgba(0, 0, 0, 0.87)",
  },
  inputFile: {
    width: "0.1px",
    height: "0.1px",
    opacity: 0,
    overflow: "hidden",
    position: "absolute",
    top: "0",
    zIndex: -1,
  },
  file: {
    height: "160px",
    position: "relative",
    overflow: "hidden",
    textAlign: "center",
  },
}));

export const Photo = (props) => {
  
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [imageSrc, setImageSrc] = useState(null);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      const isCamera = event.target.value.includes('camera');
      if (isCamera) {
        startCamera();
      } else {
        const reader = new FileReader();
        reader.onloadend = () => setImageSrc(reader.result);
        reader.readAsDataURL(file);
      }
    }
  };

  const startCamera = async () => {
    const stream = await navigator.mediaDevices.getUserMedia({ video: true });
    videoRef.current.srcObject = stream;
    videoRef.current.play();
  };

  const captureImage = () => {
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');
    const video = videoRef.current;
    context.drawImage(video, 0, 0, canvas.width, canvas.height);
    const base64 = canvas.toDataURL('image/png');
    setImageSrc(base64);
    stopCamera();
  };

  const stopCamera = () => {
    const stream = videoRef.current.srcObject;
    if (stream) {
      stream.getTracks().forEach(track => track.stop());
    }
    videoRef.current.srcObject = null;
  };

  return (
    <div>
      <input
        type="file"
        accept="image/*;capture=camera"
        onChange={handleFileChange}
      />
      {imageSrc ? (
        <img src={imageSrc} alt="预览" style={{ maxWidth: '100%', marginTop: '10px' }} />
      ) : (
        <div>
          <video ref={videoRef} style={{ width: '300px', height: 'auto' }} />
          <button onClick={captureImage}>拍照</button>
          <canvas ref={canvasRef} style={{ display: 'none' }} width="300" height="200" />
        </div>
      )}
    </div>
  );
};
