import { Box, Typography } from "@material-ui/core";
import { DatePicker } from "@material-ui/pickers";
import ConfirmModal from "components/ConfirmModal";
import TaskItemCard from "components/TaskItemCard";
import { LocalizeContext } from "i18n";
import moment from "moment";
import React from "react";

const TaskList = ({ data, status, type, submit }) => {
  const i18n = React.useContext(LocalizeContext);

  const [open, setOpen] = React.useState(null);

  const [currentTask, setCurrentTask] = React.useState("");
  const [cplDate, setCplDate] = React.useState(
    moment(new Date()).format("YYYY-MM-DD")
  );


  const handleClick = (action, data) => {
    console.log(action, data)
  };

  return (
    <Box>
      {data.map((item, index) => {
        return (
          <TaskItemCard
            data={item}
            type={type}
            status={status}
            handleClick={(action, taskName, currentTaskData) =>
              handleClick(action, taskName, currentTaskData)
            }
            key={index}
          />
        );
      })}
    </Box>
  );
};

export default TaskList;
