import { Box } from "@material-ui/core";
import { withStyles } from "@material-ui/core/styles";
import { AssignmentOutlined, ChevronRight } from "@material-ui/icons";
import { LocalizeContext } from "i18n";
import { getAwaitComplateAwaitCloseCount } from "js/util";
import PrimaryLayout from "layouts/PrimaryLayout";
import React from "react";
import { connect } from "react-redux";
import { useHistory } from "react-router-dom";
import axios from "axios";

const TaskManagermentPage = withStyles(({ spacing }) => {
  return {
    root: {
      width: `calc(100% - 16px)`,
      height: `calc(100% - 16px)`,
      display: "flex",
      flexDirection: "column",
      margin: "8px",
    },
    itemBox: {
      width: "100%",
      padding: "4px",
      margin: "4px",
      display: "flex",
      alignItems: "center",
      borderBottom: "1px solid darkgray",
    },
    itemIcon: {
      width: `24px`,
      color: "rgb(220,0,0)",
      paddingTop: "4px",
      margin: "0 4px",
    },
    discriptionBox: {
      width: `calc(100% - 54px)`,
    },
    iconBox: {
      paddingTop: "6px",
      width: "6px",
      color: "rgb(220,0,0)",
    },
    discription: {
      padding: spacing(0.5, 1),
      backgroundColor: "red",
      borderRadius: "30px",
      color: "white",
      marginLeft: spacing(1),
    },
  };
})(({ classes, taskDataGroup }) => {
  const history = useHistory();
  const i18n = React.useContext(LocalizeContext);
  const tasks = taskDataGroup.filter((task, index, self) =>
    index === self.findIndex(t => t.taskType === task.taskType)
  );


  const getToken= () => {
        // const data = {
        //   "AppId": "SCF.IQAPP",
        //   "UserName": "IQAPP",
        //   "Password": "YtLPPMGa9YUkGsJZNYJA8"
        // }

    // axios({
    //   method: 'post',  // 使用 POST 请求
    //   url: "https://ldp.sodec.cn/user/login",      
    //   data: data,  
    //   headers: {
    //     'Content-Type': 'application/json',  
    //   }
    // })
    //   .then(response => {
    //     console.log('Response:', response.data);  
    //   })
    //   .catch(error => {
    //     console.error('Error:', error);
    //   });
    const data = {
      "token_type": "Bearer",
      "expires_in": 3800,
      "not_before": null,
      "expires_on": "2024-11-26T06:34:00",
      "access_token": "eyJhbGciOiJSUzI1NiIsImtpZCI6IjBzTkoxc3d1bm5wdXJSdXltOFViaWciLCJ0eXAiOiJhdCtqd3QifQ.eyJuYmYiOjE3MzI1OTkyNDAsImV4cCI6MTczMjYwMjg0MCwiaXNzIjoiaHR0cHM6Ly9wcm94eS5zY2hpbmRsZXIuY29tL2lkZW50aXR5c3RzIiwiYXVkIjoiTkkuT0kuU2lnbmFsUi5Qcm94eS5BcGkiLCJjbGllbnRfaWQiOiJBUFAuU0NOLlNpZ25hbFIuUmV2ZXJzZS5Qcm94eSIsInNjb3BlIjpbIk5JLk9JLlNpZ25hbFIuUHJveHkuQXBpIl19.k8jrUjjPW7bxDFeISDtTsXRGneOTDJO51_yPRTzK_BJETBosaMDoMM8JbNyFtV_dReszF7cOBmQX22CG9n20jk95qR1CTGzgpbVXiQXpeNb_07MUEUPaaly8RTaGRSdMhK59-7cV-GSG8VNCvjY6YJSSzVuN9Y5CHwHQMT3Nqmr7pITz1d_PGBb63kvffnP0xh9hh3stKxAKazesKL20TwwGHUqLACEQ0WoPderSyYN1qon_gj3d_9wXNNGkMaQbiMn68EdVi9Az5DUw9E-eBYy6zb9nvXlDvEYFXQcLQjz0f02_9MVnvVhL9jGIMoipwv49RZI3gHhjWnxpEQvncQ"
    }
}

  return (
    <PrimaryLayout
      name="TasksGroupPage"
      title={i18n.INSTAPP_JOBLIST_TASKS}
      pageLevel={1}
    >
      <Box className={classes.root}>
        {tasks.map((item,index) => {
          return <Box
            key={index}
            className={classes.itemBox}
            onClick={() => {
              // history.push(`/taskList/${item.taskType}`);
              getToken()
            }}
          >
            <Box className={classes.itemIcon}>
              <AssignmentOutlined />
            </Box>
            <Box className={classes.discriptionBox}>
              {item.taskType}
              {(item.awaitStartNum +item.awaitCloseNum + item.awaitComplateNum) > 0 && (
                <span className={classes.discription}>{item.awaitStartNum +item.awaitCloseNum + item.awaitComplateNum}</span>
              )}
            </Box>
            <Box className={classes.iconBox}>
              <ChevronRight />
            </Box>
          </Box>
        })}
      
      </Box>
    </PrimaryLayout>
  );
});
export default connect((state, props) => {
  let tasks = Object.keys(state?.tasks).length > 0 ? state?.tasks : [];
  const taskList = getAwaitComplateAwaitCloseCount(tasks);
  return {
    taskDataGroup: taskList
  };
})(TaskManagermentPage);
