import axios from "axios";
import { axiosConfig, blobResponseURLs, formDataURLs, axiosIDPTConfig } from "config";

const getPostData = (data, useFormData = false) => {
  if (useFormData) {
    const formData = new FormData();
    Object.keys(data).forEach((key) => {
      if (Array.isArray(data[key])) {
        data[key].forEach((item) => formData.append(key, item));
      } else {
        formData.append(key, data[key]);
      }
    });
    return formData;
  } else {
    return JSON.stringify(data);
  }
};

const getQueryString = (data) => {
  let queryString = "";

  if (data) {
    queryString = Object.keys(data)
      .map((key) => {
        let result = `${encodeURIComponent(key)}=`;
        if (Array.isArray(data[key])) {
          result += data[key]
            .map((v) => {
              return `${encodeURIComponent(v)}`;
            })
            .join(`${"&" + key + "="}`);
        } else {
          result += `${encodeURIComponent(data[key])}`;
        }
        return result;
      })
      .join("&");
  }

  if (queryString) {
    return `?${queryString}`;
  }

  return queryString;
};

export const get = (url, data, type) => {
  return axios
    .get(url + getQueryString(data), {
      ...(!type ? axiosConfig : axiosIDPTConfig),
      responseType: blobResponseURLs.some((element) => url.startsWith(element))
        ? "blob"
        : "json",
    })
    .catch(({ message, response }) => {
      if (response.status === 400 || response.status === 403) {
        return Promise.reject({
          code: response.status,
          message: response.data.error.message,
        });
      }

      if (response.status === 401) {
        return Promise.reject({
          code: response.status,
          message: "用户凭证已过期，请重新登录",
        });
      }

      return Promise.reject({ code: response.status, message });
    });
};

export const post = (url, data) => {
  const useFormData = formDataURLs.some((element) => url.startsWith(element));
  const postData = getPostData(data, useFormData);

  return axios
    .post(url, postData, {
      ...axiosConfig,
      headers: !useFormData ? { "Content-Type": "application/json" } : {},
    })
    .catch(({ message, response }) => {
      if (response.status === 400 || response.status === 403) {
        return Promise.reject({
          code: response.status,
          message: response.data.error.message,
        });
      }

      if (response.status === 401) {
        return Promise.reject({
          code: response.status,
          message: "用户凭证已过期，请重新登录",
        });
      }

      return Promise.reject({ code: response.status, message });
    });
};


const api = axios.create({
  ...axiosIDPTConfig, ...{headers: {
    'Content-Type': 'application/json',
  }}
}
);

// export const put = async (url) => {
//   try {
//     const response = await api.put(url);
//     return response.data;
//   } catch (error) {
//     console.error('PUT request failed:', error.response || error.message);
//     throw error;
//   }
// };

const  sendPutRequest=(url, data)=> {
  return api.put(url, data)
    .then(response => {
      // 可以在这里进行响应数据的处理
      return {
        status: response.status,
        data: response.data,
        message: 'Update successful'
      };
    })
    .catch(error => {
      if (error.response) {
        // 请求已发出，且服务器响应了状态码
        console.error('Server responded with error:', error.response.data);
      } else if (error.request) {
        // 请求已发出，但没有收到响应
        console.error('No response received:', error.request);
      } else {
        // 其他错误
        console.error('Error setting up the request:', error.message);
      }
      throw error;  // 继续抛出错误，供调用者处理
    });
}


export const  put = (url,data) => {
  sendPutRequest(url, data)
    .then(response => {
      console.log(response.message, response.data);
    })
    .catch(error => {
      console.error('Error occurred:', error);
    });
}
