import {
  Box,
  Button,
  Card,
  CardActions,
  CardContent,
  Grid,
  Typography,
  withStyles,
} from "@material-ui/core";
import {
  CheckCircleOutlineOutlined,
  DescriptionOutlined
} from "@material-ui/icons";
import HourglassEmptySharpIcon from '@material-ui/icons/HourglassEmptySharp';
import { useHistory } from "react-router-dom";
import { LocalizeContext } from "i18n";
import React from "react";

const TaskListCard = withStyles(({ spacing }) => {
  return {
    button: {
      borderRadius: spacing(2.5),
      lineHeight: "unset",
      fontSize: "0.5rem",
      padding: "2px 14px",
    },
    card: {
      marginTop: spacing(2),
    },
    cardActions: {
      marginLeft: "0px",
    },
    contentColor: {
      color: "#999999",
      wordBreak: "break-word",
    },
    fontColor: {
      color: "#e54141",
      // color: "rgba(0, 0, 0, 0.54)",
    },
    iconColor: {
      color: "rgba(0, 0, 0, 0.54)",
    },
  };
})(
  ({
    classes,
    data,
    submit,
  }) => {
    const i18n = React.useContext(LocalizeContext);
    const history = useHistory();
    return (
      <Card className={classes.card}>
        <CardContent onClick={() => history.push(`/order/${data.orderNo}`)}>
          <Grid container>
              <Grid item xs={12}>
                <Box mt={1} style={{ display: "inline-flex" }}>
                  <Typography variant="body2" style={{ marginRight: "4px" }}>
                  {i18n.JOBLIST_ORDER}:
                  </Typography>
                  <Typography
                    variant="caption"
                  >
                  {data.orderNo}
                  </Typography>
                </Box>
            </Grid>
            <Grid item xs={12}>
              <Box mt={1} style={{ display: "inline-flex" }}>
                <Typography variant="body2" style={{ marginRight: "4px" }}>
                  {i18n.PROJECTLIST_PROJECT}:
                </Typography>
                <Typography
                  variant="caption"
                >
                  {data.projectName}
                </Typography>
              </Box>
            </Grid>
            <Grid item style={{display:"flex",justifyContent:'space-between'}} xs={12}>
              <Box mt={1}>
                  <Typography
                    variant="body2"
                    component="span"
                    style={{ marginRight: "4px" }}
                  >
                  {i18n.ISNTAPP_PLAN_START_DATE}:
                </Typography>
                <Typography
                  variant="caption"
                  component="span"
                  style={{ marginRight: "4px" }}
                >
                  {data.planStartDate}
                </Typography>
              </Box>
              <Box mt={1}>
                <Typography
                  variant="body2"
                  component="span"
                  style={{ marginRight: "4px" }}
                >
                  {i18n.ISNTAPP_ACTUAL_START_DATE}
                </Typography>
                <Typography
                  variant="caption"
                  component="span"
                  style={{ marginRight: "4px" }}
                >
                  
                  {data.actualStartDate}
                </Typography>
              </Box>
            </Grid>
            <Grid item style={{ display: "flex", justifyContent: 'space-between' }} xs={12}>
              <Box mt={1}>
                <Typography
                  variant="body2"
                  component="span"
                  style={{ marginRight: "4px" }}
                >
                  {i18n.ISNTAPP_PLAN_END_DATE}:
                </Typography>
                <Typography
                  variant="caption"
                  component="span"
                  style={{ marginRight: "4px" }}
                >
                  {data.planEndDate}
                </Typography>
              </Box>
              <Box mt={1}>
                <Typography
                  variant="body2"
                  component="span"
                  style={{ marginRight: "4px" }}
                >
                  {i18n.ISNTAPP_ACTUAL_END_DATE}:
                </Typography>
                <Typography
                  variant="caption"
                  component="span"
                  style={{ marginRight: "4px" }}
                >
                  {data.actualEndDate}
                </Typography>
              </Box>
            </Grid>
            <Grid item style={{ display: "flex", justifyContent: 'space-between' }} xs={12}>
              <Box mt={1}>
              </Box>
             
                {data.status === 'closed' &&
                <Box mt={1}>
                  <Typography
                  variant="body2"
                  component="span"
                  style={{ marginRight: "4px" }}
                >
                    {i18n.ISNTAPP_ACTUAL_CLOSED_DATE}:
                </Typography>
                <Typography
                  variant="caption"
                  component="span"
                  style={{ marginRight: "4px" }}
                >
                  {data.actualCloseDate}
                  </Typography>
                </Box>
               } 
            </Grid>
          </Grid>
        </CardContent>
        <CardActions
          style={{
            backgroundColor: "#f5f5f5",
            padding: "2px",
            justifyContent:"space-around"
          }}
        >
            <Button
              size="small"
              style={{
                display: data.status === "awaitComplate"?"inline-grid":"none",
              }}
              onClick={() => {
                submit("start", data);
              }}
            >
            <HourglassEmptySharpIcon
                classes={{ root: classes.iconColor }}
              />
            <Typography variant="caption">{i18n.ISNTAPP_START}</Typography>
            </Button>
            <Button
              size="small"
              style={{
                display:"inline-grid" ,
                textAlign:"center"
              }}
              onClick={() => {
              history.push(
                `/documents/${data.activityNo}/${data.orderNo}/${data?.productFamily}/listCard`
              );
            }}
            >
            <DescriptionOutlined style={{ margin: "auto" }} classes={{ root: classes.iconColor }} />
            <Typography variant="caption">{data.status === "awaitClose" ? i18n.ISNTAPP_UPLOADED  :i18n.COCKPIT_FILTER_OPEN}</Typography>
            </Button>
            <Button
              size="small"
              style={{
                display: data.status ==="closed"?"none": "inline-grid",
              }}
              onClick={() => {
                submit(data.status === "awaitComplate" ? "done" : "close", data);
              }}
            >
            <CheckCircleOutlineOutlined
              classes={{ root: classes.iconColor }}
            />
            <Typography variant="caption">{data.status === "awaitComplate" ? i18n.ISNTAPP_COMPLETE : i18n.ISNTAPP_CLOSE}</Typography>
            </Button>
        </CardActions>
      </Card>
    );
  }
);
export default TaskListCard;
