import { Box } from "@material-ui/core";
import { withStyles } from "@material-ui/core/styles";
import { AssignmentOutlined, ChevronRight } from "@material-ui/icons";
import { LocalizeContext } from "i18n";
import { getAwaitComplateAwaitCloseCount } from "js/util";
import PrimaryLayout from "layouts/PrimaryLayout";
import React from "react";
import { connect } from "react-redux";
import { useHistory } from "react-router-dom";
import axios from "axios";

const TaskManagermentPage = withStyles(({ spacing }) => {
  return {
    root: {
      width: `calc(100% - 16px)`,
      height: `calc(100% - 16px)`,
      display: "flex",
      flexDirection: "column",
      margin: "8px",
    },
    itemBox: {
      width: "100%",
      padding: "4px",
      margin: "4px",
      display: "flex",
      alignItems: "center",
      borderBottom: "1px solid darkgray",
    },
    itemIcon: {
      width: `24px`,
      color: "rgb(220,0,0)",
      paddingTop: "4px",
      margin: "0 4px",
    },
    discriptionBox: {
      width: `calc(100% - 54px)`,
    },
    iconBox: {
      paddingTop: "6px",
      width: "6px",
      color: "rgb(220,0,0)",
    },
    discription: {
      padding: spacing(0.5, 1),
      backgroundColor: "red",
      borderRadius: "30px",
      color: "white",
      marginLeft: spacing(1),
    },
  };
})(({ classes, taskDataGroup }) => {
  const history = useHistory();
  const i18n = React.useContext(LocalizeContext);
  const tasks = taskDataGroup.filter((task, index, self) =>
    index === self.findIndex(t => t.taskType === task.taskType)
  );


  const getToken= () => {
        // const data = {
        //   "AppId": "SCF.IQAPP",
        //   "UserName": "IQAPP",
        //   "Password": "YtLPPMGa9YUkGsJZNYJA8"
        // }
        // const formData = new FormData();
        // formData.append("AppId", "SCF.IQAPP");
        // formData.append("UserName", "IQAPP");
        // formData.append("Password", "YtLPPMGa9YUkGsJZNYJA8");

        const data = new FormData();
  data.append('AppId', 'SCF.IQAPP');
  data.append('UserName', 'IQAPP');
  data.append('Password', 'YtLPPMGa9YUkjiGsJZNYJA8');

  axios.post("http://ldp.sodec.cn/user/login", data, { headers: { 'Content-Type': 'application/json' } })
    .then(response => {
      console.log('Response:', response.data);
    })
    .catch(error => {
      console.error('Network Error:', error);
      // 打印更多的调试信息
      if (error.response) {
        console.error('Response Error:', error.response);
      } else if (error.request) {
        console.error('Request Error:', error.request);
      }
    });
}

  return (
    <PrimaryLayout
      name="TasksGroupPage"
      title={i18n.INSTAPP_JOBLIST_TASKS}
      pageLevel={1}
    >
      <Box className={classes.root}>
        {tasks.map((item,index) => {
          return <Box
            key={index}
            className={classes.itemBox}
            onClick={() => {
              // history.push(`/taskList/${item.taskType}`);
              getToken()
            }}
          >
            <Box className={classes.itemIcon}>
              <AssignmentOutlined />
            </Box>
            <Box className={classes.discriptionBox}>
              {item.taskType}
              {(item.awaitStartNum +item.awaitCloseNum + item.awaitComplateNum) > 0 && (
                <span className={classes.discription}>{item.awaitStartNum +item.awaitCloseNum + item.awaitComplateNum}</span>
              )}
            </Box>
            <Box className={classes.iconBox}>
              <ChevronRight />
            </Box>
          </Box>
        })}
      
      </Box>
    </PrimaryLayout>
  );
});
export default connect((state, props) => {
  let tasks = Object.keys(state?.tasks).length > 0 ? state?.tasks : [];
  const taskList = getAwaitComplateAwaitCloseCount(tasks);
  return {
    taskDataGroup: taskList
  };
})(TaskManagermentPage);
