import React, { useEffect, useState } from 'react';

export const Photo  = ({ lat, lng }) => {
  const [address, setAddress] = useState('');
  const location ={ longitude: "121.197237", latitude: "31.449172" }
  useEffect(() => {
    if (lat && lng) {
      const map = new window.BMap.Map('container');
      const geocoder = new window.BMap.Geocoder();

      const point = new window.BMap.Point("121.197237", "31.449172");
      geocoder.getLocation(point, (result) => {
        if (result) {
          setAddress(result.address);
        } else {
          setAddress('无法获取地址');
        }
      });
    }
  }, [lat, lng]);

  return (
    <div>
      <div id="container" style={{ height: '0', width: '0' }}></div>
      <p>地址: {address}</p>
    </div>
  );
};
