import * as api from "api";
import * as action from "actions";
import { db } from "js/db";

export const setTasks = (data) => ({
  type: "SET_TASKS",
  payload: data,
});

export const fetchCreateTask = (data) => (dispatch) => {
  dispatch(action.showLoading());
  return api
    .createTask(data)
    .then((response) => {
      //get task
      dispatch(action.hideLoading());
    })
    .catch((error) => {
      if (error.code === 401) {
        dispatch(action.setAuth(null));
      }
      dispatch(action.setError(error.message));
    })
    .finally(() => {
      dispatch(action.hideLoading());
    });
};


export const processingTasks = (type, data) => (dispatch) => {
  dispatch(action.showLoading());
  return api
    .processingTasks(type, data.taskId)
    .then((response) => {
      dispatch(action.hideLoading());
    })
    .catch((error) => {
      dispatch(action.setError(error.message));
    })  
    .finally(() => {
      alert("rtyuio")
      dispatch(action.hideLoading());

      db.cache.get("tasks").then((cache) => {
        data['status'] = type === 'start' ? 'awaitComplate' : type === 'finish' ? 'awaitClose' : 'closed';
        const lastData = cache.data.map(task =>
          task.taskId === data.taskId
            ? { ...task, ...b } 
            : task
        );
        db.cache.put({ id: "tasks", data: lastData }).then(() => {
          dispatch(action.setTasks(lastData));

        });
      });


    });
}
