import React, { useRef, useState } from 'react';

const Photo = () => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [isCameraActive, setIsCameraActive] = useState(false);
  const [imageSrc, setImageSrc] = useState(null);

  const startCamera = async () => {
    if (videoRef.current) {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({
          video: true,
        });
        videoRef.current.srcObject = stream;
        videoRef.current.play();
        setIsCameraActive(true);
      } catch (error) {
        console.error("Error accessing the camera: ", error);
      }
    }
  };

  const captureImage = () => {
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');
    const video = videoRef.current;

    // 设置 canvas大小
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;

    // 绘制视频帧到 canvas
    context.drawImage(video, 0, 0, canvas.width, canvas.height);

    // 可以在 canvas 上添加定位框
    const x = canvas.width / 4; // 定位框 X 坐标
    const y = canvas.height / 4; // 定位框 Y 坐标
    const width = canvas.width / 2; // 定位框宽度
    const height = canvas.height / 2; // 定位框高度

    // 绘制定位框
    context.strokeStyle = 'red'; // 框的颜色
    context.lineWidth = 5; // 框的宽度
    context.strokeRect(x, y, width, height);

    // 获取图像数据
    const base64 = canvas.toDataURL('image/png');
    setImageSrc(base64);
    stopCamera();
  };

  const stopCamera = () => {
    const stream = videoRef.current.srcObject;
    if (stream) {
      stream.getTracks().forEach(track => track.stop());
    }
    videoRef.current.srcObject = null;
    setIsCameraActive(false);
  };

  return (
    <div style={{ textAlign: 'center' }}>
      <button onClick={startCamera} style={{ marginBottom: '10px' }}>
        启动摄像头
      </button>
      {isCameraActive ? (
        <div style={{ position: 'relative', display: 'inline-block' }}>
          <video ref={videoRef} style={{ width: '300px', height: 'auto', border: '2px solid #fff' }} />
          <button onClick={captureImage} style={{ marginTop: '10px' }}>
            拍照
          </button>
          <canvas ref={canvasRef} style={{ display: 'none' }} />
          {/* 定位框 */}
          <div style={{
            position: 'absolute',
            top: '25%',
            left: '25%',
            width: '50%',
            height: '50%',
            border: '2px solid red',
            pointerEvents: 'none', // 确保框不会干扰点击
          }} />
        </div>
      ) : imageSrc ? (
        <img src={imageSrc} alt="预览" style={{ maxWidth: '100%', marginTop: '10px' }} />
      ) : null}
    </div>
  );
};

export default Photo;
