import React, { useRef, useEffect } from 'react';

const CameraComponent = () => {
    const videoRef = useRef(null);
    const canvasRef = useRef(null);
    const photoRef = useRef(null);

    useEffect(() => {
        const getUserMedia = async () => {
            try {
                const stream = await navigator.mediaDevices.getUserMedia({ video: true, audio: false });
                if (videoRef.current) {
                    videoRef.current.srcObject = stream;
                }
            } catch (error) {
                console.error("获取摄像头权限失败:", error);
            }
        };

        getUserMedia();

        return () => {
            const stream = videoRef.current?.srcObject;
            if (stream) {
                const tracks = stream.getTracks();
                tracks.forEach(track => track.stop());
            }
        };
    }, []);

    const capturePhoto = () => {
        const canvas = canvasRef.current;
        const context = canvas.getContext('2d');
        const video = videoRef.current;

        // 设置画布尺寸与视频相同
        canvas.width = video.videoWidth;
        canvas.height = video.videoHeight;

        // 将视频绘制到画布上
        context.drawImage(video, 0, 0);

        // 将画布内容转为图片并显示
        const photo = photoRef.current;
        photo.src = canvas.toDataURL();
        photo.style.display = 'block';
    };

    return (
        <div>
            <div id="cameraContainer" style={{
                position: 'relative',
                width: '345px',
                height: '210px',
                overflow: 'hidden'
            }}>
                <video ref={videoRef} width="345" height="210" autoPlay style={{ objectFit: 'cover' }}></video>
                <div className="frame-container" style={{
                    position: 'absolute',
                    top: 0,
                    left: 0,
                    width: '100%',
                    height: '100%'
                }}>
                    <div className="mask" style={{
                        position: 'absolute',
                        width: '100%',
                        height: '100%'
                    }}></div>
                    <div id="frame" style={{
                        position: 'absolute',
                        top: '50%',
                        left: '50%',
                        transform: 'translate(-50%, -50%)',
                        width: '200px',
                        height: '90px',
                        zIndex: 10,
                        backgroundColor: 'transparent'
                    }}>
                        <div className="corner topLeft" style={{
                            position: 'absolute',
                            borderColor: 'red',
                            borderStyle: 'solid',
                            padding: '6px',
                            top: '1px',
                            left: '1px',
                            borderWidth: '2px 0 0 2px'
                        }}></div>
                        <div className="corner topRight" style={{
                            position: 'absolute',
                            borderColor: 'red',
                            borderStyle: 'solid',
                            padding: '6px',
                            top: '1px',
                            right: '1px',
                            borderWidth: '2px 2px 0 0'
                        }}></div>
                        <div className="corner bottomLeft" style={{
                            position: 'absolute',
                            borderColor: 'red',
                            borderStyle: 'solid',
                            padding: '6px',
                            bottom: '1px',
                            left: '1px',
                            borderWidth: '0 0 2px 2px'
                        }}></div>
                        <div className="corner bottomRight" style={{
                            position: 'absolute',
                            borderColor: 'red',
                            borderStyle: 'solid',
                            padding: '6px',
                            bottom: '1px',
                            right: '1px',
                            borderWidth: '0 2px 2px 0'
                        }}></div>
                    </div>
                    <div style={{ marginTop: '6px', textAlign: 'center', color: 'red' }}>
                        Please put your ID in the box
                    </div>
                </div>
            </div>
            <button id="captureButton" onClick={capturePhoto} style={{ marginTop: '10px' }}>
                拍照
            </button>
            <canvas ref={canvasRef} style={{ display: 'none' }}></canvas>
            <img ref={photoRef} id="photo" alt="Captured Photo" style={{ display: 'none', width: '345px', height: '210px' }} />
        </div>
    );
};

export default CameraComponent;
