import { Box } from "@material-ui/core";
import Fab from "@material-ui/core/Fab";
import AddIcon from "@material-ui/icons/Add";
import * as action from "actions";
import FFTab from "components/FFTab";
import TaskListItem from "components/TaskListItem";
import { LocalizeContext } from "i18n";
import * as repo from "js/fetch";
import { getTaskCount } from "js/util";
import PrimaryLayout from "layouts/PrimaryLayout";
import React from "react";
import { connect } from "react-redux";
import { useHistory } from "react-router-dom";
import { taskCleaner } from "js/publicFn";
import { useParams } from 'react-router-dom';

const Tasks = ({ type, tasksData, submit }) => {
  const history = useHistory();
  const i18n = React.useContext(LocalizeContext);
  const [status, setStatus] = React.useState("open");
  const [taskCount, setTaskCount] = React.useState({awaitComplateNum:0,awaitCloseNum:0});
  const params = useParams()
  console.log(tasksData)
  const tabs = [
    { title: i18n.ISNTAPP_TO_COMPLETE, value: "open" },
    { title: i18n.ISNTAPP_TO_CLOSE, value: "pending" },
    { title: i18n.ISNTAPP_CLOSED, value: "close" },
  ];

  let currentStatusTasks = [];

  if (status === "open") {
    tasksData.forEach((item) => {
      if (item.Status === "待完成") {
        currentStatusTasks.push(item);
        setTaskCount()
      }
    });
  }

  if (status === "pending") {
    tasksData.forEach((item) => {
      if (item.Status === "待关闭") {
        currentStatusTasks.push(item);
      }
    });
  }

  if (status === "close") {
    tasksData.forEach((item) => {
      if (item.Status === "已关闭") {
        currentStatusTasks.push(item);
      }
    });
  }

  currentStatusTasks.sort((a, b) =>
    a.DueDate ? (a.DueDate > b.DueDate ? 1 : -1) : 1
  );

  const pending = getTaskCount().awaitCloseNum;
  const open = getTaskCount().awaitComplateNum;
  return (
    <PrimaryLayout name="tasks" title={params.type}>
      <Box
        style={{
          position: "fixed",
          top: "52px",
          width: "100%",
          zIndex: "10000",
        }}
      >
        <FFTab
          tabs={tabs}
          selectedTabValue={"open"}
          onTabChange={(value) => setStatus(value)}
        />
        <span
          style={{
            position: "absolute",
            top: "0",
            left: "22%",
            padding: "2px 8px",
            backgroundColor: "red",
            borderRadius: "40px",
            color: "white",
          }}
        >
          2
        </span>
            <span
              style={{
                position: "absolute",
                top: "0",
                left: "55%",
                padding: "2px 8px",
                backgroundColor: "red",
                borderRadius: "40px",
                color: "white",
              }}
            >
              2
            </span>
      </Box>

      <Box
        style={{
          width: "100%",
          padding: "16px",
          position: "absolute",
          top: "32px",
          height: window.screen.height - 157 + "px",
          overflow: "hidden auto",
        }}
      >
        <TaskListItem
          data={currentStatusTasks}
          status={status}
          type={type}
          submit={(submitDatas) => { 
            submit(submitDatas);
          }}
        ></TaskListItem>
      </Box>
    </PrimaryLayout>
  );
};

const mapStateToProps = (state, ownProps) => {
  const { match } = ownProps;
  const { params } = match;
  const { type } = params;
  const { tasks } = state;
  
  let taskList = Object.keys(tasks).length > 0 ? tasks : [];
  return {
    type,
    tasksData:
      taskList.filter((item) =>  item.Task_type ===type),
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    submit: (submitDatas) => {
      dispatch(action.fetchCreateTask([submitDatas])).then((response) => {
        repo.fetchTasks().then((data) => {
          dispatch(action.setTasks(data));
        });
      });
    },
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Tasks);
