import {
  Box,
  Button,
  Card,
  CardActions,
  CardContent,
  Grid,
  Typography,
  withStyles,
} from "@material-ui/core";
import {
  AssignmentTurnedInOutlined,
  CancelOutlined,
  CheckCircleOutlineOutlined,
  DeleteForeverOutlined,
  EventAvailableOutlined,
  EventNoteOutlined,
  ForwardOutlined,
  DescriptionOutlined
} from "@material-ui/icons";
import HourglassEmptySharpIcon from '@material-ui/icons/HourglassEmptySharp';
import { LocalizeContext } from "i18n";
import React from "react";
import { connect } from "react-redux";

const TaskListCard = withStyles(({ spacing }) => {
  return {
    button: {
      borderRadius: spacing(2.5),
      lineHeight: "unset",
      fontSize: "0.5rem",
      padding: "2px 14px",
    },
    card: {
      marginTop: spacing(2),
    },
    cardActions: {
      marginLeft: "0px",
    },
    contentColor: {
      color: "#999999",
      wordBreak: "break-word",
    },
    fontColor: {
      color: "#e54141",
      // color: "rgba(0, 0, 0, 0.54)",
    },
    iconColor: {
      color: "rgba(0, 0, 0, 0.54)",
    },
  };
})(
  ({
    classes,
    data,
    handleClickOpen,
  }) => {
    const i18n = React.useContext(LocalizeContext);
    return (
      <Card className={classes.card}>
        <CardContent>
          <Grid container>
              <Grid item xs={12}>
                <Box mt={1} style={{ display: "inline-flex" }}>
                  <Typography variant="body2" style={{ marginRight: "4px" }}>
                  {i18n.JOBLIST_ORDER}:
                  </Typography>
                  <Typography
                    variant="body2"
                    classes={{ root: classes.contentColor }}
                  >
                  {data.orderNo}
                  </Typography>
                </Box>
            </Grid>
            <Grid item xs={12}>
              <Box mt={1} style={{ display: "inline-flex" }}>
                <Typography variant="body2" style={{ marginRight: "4px" }}>
                  {i18n.PROJECTLIST_PROJECT}:
                </Typography>
                <Typography
                  variant="body2"
                  classes={{ root: classes.contentColor }}
                >
                  {data.projectName}
                </Typography>
              </Box>
            </Grid>
            <Grid item style={{display:"flex",justifyContent:'space-between'}} xs={12}>
              <Box mt={1}>
                  <Typography
                    variant="body2"
                    component="span"
                    style={{ marginRight: "4px" }}
                  >
                  {i18n.ISNTAPP_PLAN_START_DATE}:
                </Typography>
                <Typography
                  variant="caption"
                  component="span"
                  style={{ marginRight: "4px" }}
                >
                  {data.planStartDate}
                </Typography>
              </Box>
              <Box mt={1}>
                <Typography
                  variant="body2"
                  component="span"
                  style={{ marginRight: "4px" }}
                >
                  {`实际开始日期:`}
                </Typography>
                <Typography
                  variant="caption"
                  component="span"
                  style={{ marginRight: "4px" }}
                >
                  
                  {data.actualStartDate}
                </Typography>
              </Box>
            </Grid>
            <Grid item style={{ display: "flex", justifyContent: 'space-between' }} xs={12}>
              <Box mt={1}>
                <Typography
                  variant="body2"
                  component="span"
                  style={{ marginRight: "4px" }}
                >
                  {i18n.ISNTAPP_PLAN_END_DATE}:
                </Typography>
                <Typography
                  variant="caption"
                  component="span"
                  style={{ marginRight: "4px" }}
                >
                  {data.planEndDate}
                </Typography>
              </Box>
              <Box mt={1}>
                <Typography
                  variant="body2"
                  component="span"
                  style={{ marginRight: "4px" }}
                >
                  {i18n.ISNTAPP_ACTUAL_END_DATE}:
                </Typography>
                <Typography
                  variant="caption"
                  component="span"
                  style={{ marginRight: "4px" }}
                >
                  {data.actualEndDate}
                </Typography>
              </Box>
            </Grid>
            <Grid item style={{ display: "flex", justifyContent: 'space-between' }} xs={12}>
              <Box mt={1}>
              </Box>
             
                {data.status === 'closed' &&
                <Box mt={1}>
                  <Typography
                  variant="body2"
                  component="span"
                  style={{ marginRight: "4px" }}
                >
                    {i18n.ISNTAPP_ACTUAL_CLOSED_DATE}:
                </Typography>
                <Typography
                  variant="caption"
                  component="span"
                  style={{ marginRight: "4px" }}
                >
                  {data.actualCloseDate}
                  </Typography>
                </Box>
               } 
            </Grid>
          </Grid>
        </CardContent>
        <CardActions
          style={{
            backgroundColor: "#f5f5f5",
            padding: "2px",
            justifyContent:"space-around"
          }}
        >
            <Button
              size="small"
              style={{
                display: "inline-grid",
              }}
              onClick={() => {
                handleClickOpen("done", data.Description, data);
              }}
            >
            <HourglassEmptySharpIcon
                classes={{ root: classes.iconColor }}
              />
            <Typography variant="caption">{i18n.JOBLIST_ORDER}:</Typography>
            </Button>
            <Button
              size="small"
              style={{
                display: "inline-grid",
                textAlign:"center"
              }}
              onClick={() => {
                handleClickOpen("delete", data.Description, data);
              }}
            >
            <DescriptionOutlined style={{ margin: "auto" }} classes={{ root: classes.iconColor }} />
            <Typography variant="caption">{data.status === 'closed' ? i18n.ISNTAPP_JOBLIST_DOC_DONE  :i18n.COCKPIT_FILTER_OPEN}</Typography>
            </Button>
            <Button
              size="small"
              style={{
                display: "inline-grid",
              }}
              onClick={() => {
                handleClickOpen("close", data.Description, data);
              }}
            >
            <CheckCircleOutlineOutlined
              classes={{ root: classes.iconColor }}
            />
            <Typography variant="caption">{i18n.ISNTAPP_COMPLETE}</Typography>
            </Button>
        </CardActions>
      </Card>
    );
  }
);
export default (TaskListCard);
